import React,{useEffect,useRef,useState} from 'react'
import '../src/index.css'
import GSAP from 'gsap'
import ethers from 'ethers'
function Main(){
    let eff=useRef();
    let par=useRef();
    let prt=useRef();
    const [greet,setgreet]=useState('Connect MetaMask');
    const [connect,setconnect]=useState();
    function mask(){
        let account;
        if(window.ethereum){
            window.ethereum.request({method:'eth_requestAccounts'}).then(accounts=>{
                account=accounts[0];
                setText(account);
                setgreet("HAPPY DIWALI");
                
            })
        }
        else{
            alert('Please Install MetaMask')
        }
        
    }
    useEffect(()=>{GSAP.from(eff,{opacity:0,x:40,duration:2})},[]);
    useEffect(()=>{GSAP.from(par,{opacity:0,y:-40,duration:2})});
    useEffect(()=>{GSAP.from(prt,{scale:10,x:40,duration:1})});
    return(
        <div className='Frame' >
            <h1 className='message' ref={el=>eff=el}>{greet}</h1>
            <h2 ref={el=>prt=el}>{text}</h2>
            <div class='work'>
                <button className='button' ref={el=>par=el} onClick={mask}>{connect}</button>
            </div>
            {/* <p>Enter your message</p> */}
        </div>
    )
}
export default Main;
