import React,{useEffect,useRef} from 'react'
import '../src/index.css'
import GSAP from 'gsap'
function Main(){
    let eff=useRef();
    let par=useRef();
    useEffect(()=>{GSAP.from(eff,{opacity:0,x:40,duration:2})},[]);
    useEffect(()=>{GSAP.from(par,{opacity:0,x:-40,duration:2})});
    return(
        <div className='Frame' >
            <h1 className='message' ref={el=>eff=el} >Hi</h1>
            <div class='work'>
                <button className='button' ref={el=>par=el}>Connect Metamask</button>
            </div>
            <p>Enter your message</p>
        </div>
    )
}
export default Main;
